<?php echo form_open($this->lib_url->getUrl() ); ?><div class="after"><table class="table_view_cart" cellpadding="6" cellspacing="1" style="width:100%" border="0"><tr>    <th>Hình ảnh</th>    <th>Tên sản phẩm</th>    <th>Số lượng</th>    <th style="text-align:right">Giá</th>    <th style="text-align:right">Tổng giá</th>    <th style="text-align:center">Xóa</th></tr><?php $i = 1; ?><?php foreach ($this->cart->contents() as $items): ?>	<?php echo form_hidden('c'.$i.'[rowid]', $items['rowid']); ?>	<tr>	  <td width="10px"><img src="<?php echo $items['images']?>"></td>	  <td>			<a href="<?php echo $items['url']?>" target="_blank"><?php echo $items['name']; ?></a>	  </td>      <td width="10px" align="center"><?php echo form_input(array('name' => 'c'.$i.'[qty]', 'value' => $items['qty'], 'maxlength' => '2', 'size' => '2')); ?></td>	  <td style="text-align:right"><?php echo $this->cart->format_number($items['price']); ?></td>	  <td style="text-align:right">$<?php echo $this->cart->format_number($items['subtotal']); ?></td>      <td align="center" width="10px"><a class="del" href="<?php echo base_url('/cart/del/'.$items['rowid'])?>">Xóa</a></td>	</tr><?php $i++; ?><?php endforeach; ?><tr>  <td colspan="3"> </td>    <td align="right"><strong>Tổng giá</strong></td>  <td align="right">$<?php echo $this->cart->format_number($this->cart->total()); ?></td>  <td> </td></tr></table><p class="rfloat"><a class="" href="#">Hướng dẫn mua hàng</a><?php echo form_submit('submit_update_cart', 'Cập nhật giỏ hàng'); ?><input type="button" value="Đặt hàng ngay" class="" onclick="return scroll2tag('.content_payment_info')" /></p></div><?php if($this->cart->total_items() >0 ) $this->load->view('form_info_customer'); ?>